import { prefix } from '../constants';
/** 关闭加载动画 */
export var closeFetchLoading = function () {
    var hideContainer = document.getElementsByClassName("".concat(prefix, "-antd-loading"));
    Array.from(hideContainer).forEach(function (el) {
        document.body.removeChild(el);
    });
};
